if (typeof ANMatrix == 'undefined') {
    ANMatrix = require('./base.js');
}

ANMatrix.Matrix.prototype.root = function(power) {
    var basis = null, normal = null, basisInv = null;
    
    var diag = this.diagonalize();
    if (!diag) {
        var jordan = this.jordan();
        if (!jordan) return null;
        basis = jordan.P;
        basisInv = jordan.Pinv;
        normal = jordan.J;
    } else {
        basis = diag.S;
        normal = diag.V;
        basisInv = diag.Sinv;
    }
    
}
